.section .rodata
resstring:
.asciz "%d\n"

.text
.align  2
.global main
.type   main, %function
main:
        stmfd   sp!, {r4, lr}
	mov	icount, 2
mloop:
	add_and_test vector1 vector2
	add_and_test vector2 vector1
	b	mloop
printme:
	mov	r1, icount
        ldr     r0, =resstring
        bl      printf

	mov	r0, 0
        ldmfd   sp!, {r4, pc}
        mov     r7, 1           @ set r7 to 1 - the syscall for exit
        swi     0               @ then invoke the syscall from linux
