.macro copy_vector a b
	ldr	r0, =\a
	add	r0, r0, 1
	mov	r1, VSIZE
	ldr	r2, =\b
	bl	copybytes
.endm

# this macro lifted from test_add_digit_strings.s 
# the usage of add_digit_strings is tricky because
# the fifth parameter must be passed on the stack

.macro add_strings a al b bl c
#        stmfd   sp!, {r4}       /* stash r4 on the stack - we destroy it in add_digit_strings */
        stp fp, lr, [sp, #-0x40]!
        stp x4, x5, [sp, #0x10]
        stp x6, x7, [sp, #0x20]
        stp x8, x9, [sp, #0x30]
        mov fp, sp

        ldr     r0, =\c
#        stmfd   sp!, {r0}       /* this is the fifth parameter for the subroutine */
        stp fp, lr, [sp, #-0x40]!
        stp x4, x5, [sp, #0x10]
        stp x6, x7, [sp, #0x20]
        stp x8, x9, [sp, #0x30]
        mov fp, sp

        ldr     r0, =\a
        ldr     r1, =\al
        ldr     r2, =\b
        ldr     r3, =\bl
        bl      add_digit_strings
        add     sp, sp, 4       /* revert sp to before (1) */
#        ldmfd   sp!, {r4}      /* and get stashed r4 */
        ldp x8, x9, [sp, #0x30]
        ldp x6, x7, [sp, #0x20]
        ldp x4, x5, [sp, #0x10]
        ldp fp, lr, [sp], #0x40
.endm

.macro add_and_test ivector ovector
	add_strings \ovector VSIZE \ivector VSIZE output
	copy_vector output \ovector
	add	icount, icount, 1
	ldr	vptr, =\ovector
	ldrb	tmp, [vptr]
	teq	tmp, 0
	bne	printme
.endm

.equ	VSIZE, 1000

.align 4

vptr		.req r2
tmp		.req r3
icount		.req r4

