.section .rodata
resstring:
.asciz "%d\n"

.text
.align  2
.global main
.type   main, %function
main:
#        stmfd   sp!, {r4, lr}
#        stp fp, lr, [sp, #-0x40]!
#        stp x4, x5, [sp, #0x10]
#        stp x6, x7, [sp, #0x20]
#        stp x8, x9, [sp, #0x30]
        stp fp, lr, [sp, #-0x10]!
        mov fp, sp

	mov	icount, 2
mloop:
	add_and_test vector1 vector2
	add_and_test vector2 vector1
	b	mloop
printme:
	mov	x1, icount
        ldr     x0, =resstring
        bl      printf

#        ldmfd   sp!, {r4, pc}
#        ldp x8, x9, [sp, #0x30]
#        ldp x6, x7, [sp, #0x20]
#        ldp x4, x5, [sp, #0x10]
#        ldp fp, lr, [sp], #0x40
        ldp fp, lr, [sp], #0x10

	mov	x0, #0		/* exit code to 0 */
	mov     w8, #93		/* set w8 to 93 - the syscall for exit */
        svc	#0		/* then invoke the syscall from linux */
