# this computes projecteuler.net problem 025

.macro copy_vector a b
	ldr	x0, =\a
	add	x0, x0, 1
	mov	x1, VSIZE
	ldr	x2, =\b
	bl	copybytes
.endm

.macro add_strings a al b bl c
        ldr     x0, =\a
        ldr     x1, =\al
        ldr     x2, =\b
        ldr     x3, =\bl
        ldr     x4, =\c

        bl      add_digit_strings
.endm

.macro add_and_test ivector ovector
	add_strings \ovector VSIZE \ivector VSIZE output
	copy_vector output \ovector
	add	icount, icount, 1
	ldr	vptr, =\ovector
	ldrb	tmp, [vptr]
	cmp	tmp, 0
	b.ne	printme
.endm

.equ	VSIZE, 1000

.align 4

vptr		.req x18
tmp		.req w19
icount		.req x20

