.align  2
.global main
.type   main, %function
main:
        stp	fp, lr, [sp, #-0x10]!
        mov	fp, sp

	mov	icount, 2
mloop:
	add_and_test vector1 vector2
	add_and_test vector2 vector1
	b	mloop
printme:
